;(function($){$.fn.sizingrotator=function(options){var infinityPushWrapper=this;return this.each(function(){var rotatorWrap=$(this);rotatorSelectors=$('.selectors');rotatorSelectorsLink=$('.selectors li a');rotatorImage=$('.rotating_images');rotatorText=$('.rotating_txt');rotatorSelectors.find('li').first().find('a').addClass('selected');rotatorSelectorsLink.click(function(){var getIndex=$(this).parent().index();if($(this).hasClass('selected')){return false}else{$(this).parent().siblings().find('.selected').removeClass('selected');rotatorImage.find('li').hide().eq(getIndex).show();rotatorText.find('li').hide().eq(getIndex).show();$(this).addClass('selected')}return false})})}})(jQuery);